import React, { useState, useEffect } from 'react';
import '../styles/login.css';

const GestionConsejo = () => {
    const [nombre, setNombre] = useState('');
    const [origen, setOrigen] = useState('');
    const [miembroSeleccionado, setMiembroSeleccionado] = useState('');
    const [miembrosConsejo, setMiembrosConsejo] = useState([]);

    useEffect(() => {
        // L贸gica para obtener la lista de miembros del Consejo
        fetch('http://localhost:5000/consejo')
            .then(response => response.json())
            .then(data => setMiembrosConsejo(data))
            .catch(error => console.error('Error al obtener la lista de miembros:', error));
    }, []);

    const handleNombreChange = (e) => {
        setNombre(e.target.value);
    };

    const handleOrigenChange = (e) => {
        setOrigen(e.target.value);
    };

    const handleMiembroSeleccionadoChange = (e) => {
        setMiembroSeleccionado(e.target.value);
    };

    const handleCrearMiembroSubmit = (e) => {
        e.preventDefault();

        if (!nombre || !origen) {
            alert('Por favor, completa todos los campos');
            return;
        }

        // L贸gica para enviar los datos al backend y crear el miembro del Consejo
        fetch('http://localhost:5000/consejo', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ nombre, origen }),
        })
            .then(response => response.json())
            .then(data => {
                console.log('Respuesta del servidor:', data);
                // Actualizar el estado local con el nuevo miembro del Consejo
                setMiembrosConsejo(prevMiembros => [...prevMiembros, data.nuevoMiembro]);
                // Limpiar los campos del formulario
                setNombre('');
                setOrigen('');
            })
            .catch(error => {
                console.error('Error al enviar datos al backend:', error);
            });
    };

    const handleBorrarMiembroSubmit = (e) => {
        e.preventDefault();

        if (!miembroSeleccionado) {
            alert('Por favor, selecciona un miembro para borrar');
            return;
        }

        // L贸gica para enviar la solicitud al backend y borrar el miembro del Consejo
        fetch(`http://localhost:5000/consejo/${miembroSeleccionado}`, {
            method: 'DELETE',
        })
            .then(response => response.json())
            .then(data => {
                console.log('Respuesta del servidor:', data);
                // Actualizar el estado local eliminando el miembro del Consejo borrado
                setMiembrosConsejo(prevMiembros =>
                    prevMiembros.filter(miembro => miembro.id !== miembroSeleccionado)
                );
                // Limpiar el miembro seleccionado
                setMiembroSeleccionado('');
            })
            .catch(error => {
                console.error('Error al enviar solicitud de borrado al backend:', error);
            });
    };

    return (
        <div>
            <div className="py-5">
                <div className="login-container">
                    <h2 className="pb-3">Crear Miembro del Consejo</h2>
                    <form action="#" method="post" onSubmit={handleCrearMiembroSubmit}>
                        <label htmlFor="nombre">Nombre del miembro:</label>
                        <input
                            type="text"
                            id="nombre"
                            name="nombre"
                            value={nombre}
                            onChange={handleNombreChange}
                            required
                        />
                        <label htmlFor="origen">Origen del miembro (Profesor, AMPA...):</label>
                        <input
                            type="text"
                            id="origen"
                            name="origen"
                            value={origen}
                            onChange={handleOrigenChange}
                            required
                        />
                        <div className="div pt-4">
                            <input type="submit" value="Crear Miembro" />
                        </div>
                    </form>
                </div>
            </div>
            <div className="py-5">
                <div className="login-container">
                    <h2 className="pb-3">Borrar Miembro del Consejo</h2>
                    <form action="#" method="post" onSubmit={handleBorrarMiembroSubmit}>
                        <label htmlFor="miembro">Seleccionar miembro:</label>
                        <select
                            name="miembro"
                            id="miembro"
                            value={miembroSeleccionado}
                            onChange={handleMiembroSeleccionadoChange}
                        >
                            <option value="">Selecciona un miembro</option>
                            {/* Mapear los miembros del Consejo desde el estado local */}
                            {miembrosConsejo.map(miembro => (
                                <option key={miembro.id} value={miembro.id}>
                                    {miembro.nombre}
                                </option>
                            ))}
                        </select>
                        <div className="div pt-4">
                            <input type="submit" value="Borrar miembro" />
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default GestionConsejo;
