import React, { useState, useEffect } from 'react';
import './styles/index.css';
import Routes from './routes';
import Footer from './components/footer/footer';
import Header from './components/header/header';
import HeaderLogin from './components/header/headerLogin';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Aquí podrías realizar alguna lógica para verificar si el usuario tiene un token JWT válido
    // Puedes utilizar localStorage, sessionStorage o alguna llamada al servidor para verificar el token
    const token = localStorage.getItem('token'); // Obtén el token del almacenamiento local

    if (token) {
      // Si hay un token, podrías realizar una verificación adicional en el servidor
      // o simplemente asumir que el token es válido por ahora
      setIsAuthenticated(true);
    }
  }, []);

  return (
    <div className="App fondo">
      {isAuthenticated ? <HeaderLogin /> : <Header />}
      <Routes />
      <Footer />
    </div>
  );
}

export default App;
