import React, { useState, useEffect } from 'react';

const Carousel = () => {
    const [imagenes, setImagenes] = useState([]);

    useEffect(() => {
        // Lógica para obtener la lista de imágenes (puedes hacer una solicitud al backend aquí)
        const obtenerImagenes = async () => {
            try {
                const response = await fetch('http://localhost:5000/imagenes');
                if (response.ok) {
                    const data = await response.json();
                    setImagenes(data);
                } else {
                    console.error('Error al obtener la lista de imágenes');
                }
            } catch (error) {
                console.error('Error al obtener la lista de imágenes:', error);
            }
        };

        obtenerImagenes();
    }, []);

    // Ordena las imágenes por ID de manera descendente y toma la primera como activa
    const imagenesOrdenadas = imagenes.sort((a, b) => b.id - a.id);
    const imagenActiva = imagenesOrdenadas.length > 0 ? imagenesOrdenadas[0] : null;

    return (
        <div className="text-center fondo">
            <div id="carouselExample" className="carousel slide" data-bs-ride="carousel" data-bs-interval="4000">
                <div className="carousel-inner">
                    {imagenes.map((imagen, index) => (
                        <div key={imagen.id} className={`carousel-item ${imagen === imagenActiva ? 'active' : ''}`}>
                            <img src={`http://localhost:5000/uploads/${imagen.foto}`} className="img-fluid img-instalaciones" alt={imagen.nombre} />
                            <div className="carousel-caption">
                                <h1>{imagen.nombre}</h1>
                            </div>
                        </div>
                    ))}
                </div>
                <a className="carousel-control-prev" href="#carouselExample" role="button" data-bs-slide="prev">
                    <span className="carousel-control-prev-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Anterior</span>
                </a>
                <a className="carousel-control-next" href="#carouselExample" role="button" data-bs-slide="next">
                    <span className="carousel-control-next-icon" aria-hidden="true"></span>
                    <span className="visually-hidden">Siguiente</span>
                </a>
            </div>
        </div>
    );
};

export default Carousel;
