import React, { useState } from 'react';
import '../styles/login.css';
import { Link } from 'react-router-dom';

const Login = () => {
  const [usuario, setUsuario] = useState('');
  const [contrasena, setContrasena] = useState('');

  const handleLoginSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:5000/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ usuario, contrasena }),
      });

      if (response.ok) {
        const data = await response.json();
        const { token } = data;
        
        // Guardar el token en el almacenamiento local (puedes usar cookies o el contexto para un manejo más avanzado)
        localStorage.setItem('token', token);

        // Redireccionar o realizar otras acciones después del inicio de sesión exitoso
        // Por ejemplo, redireccionar a "/homeUsuario"
      } else {
        const errorData = await response.json();
        alert(`Error: ${errorData.error}`);
      }
    } catch (error) {
      console.error('Error al enviar datos al backend:', error);
    }
  };

  return (
    <div className="py-5">
      <div className="login-container">
        <h2 className="pb-3">Iniciar Sesión</h2>
        <form action="#" method="post" onSubmit={handleLoginSubmit}>
          <label htmlFor="usuario">Usuario:</label>
          <input
            type="text"
            id="usuario"
            name="usuario"
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
            required
          />
          <label htmlFor="contrasena">Contraseña:</label>
          <input
            type="password"
            id="contrasena"
            name="contrasena"
            value={contrasena}
            onChange={(e) => setContrasena(e.target.value)}
            required
          />
          <div className="div pt-4">
            <input type="submit" value="Iniciar Sesión" />
          </div>
        </form>
      </div>
    </div>
  );
};

export default Login;
